import { AccountId, Header } from '@polkadot/types';
import { AnyJsonObject } from '@polkadot/types/types';
/**
 * @name HeaderExtended
 * @description
 * A [[Block]] header with an additional `author` field that indicates the block author
 */
export default class HeaderExtended extends Header {
    private _author?;
    constructor(header?: Header | null, sessionValidators?: AccountId[]);
    /**
     * @description Convenience method, returns the author for the block
     */
    readonly author: AccountId | undefined;
    /**
     * @description Creates the JSON representation
     */
    toJSON(): AnyJsonObject;
}
