describe('ExtrinsicExtraV2', () => {
  it('encodes to a sane Uint8Array (default construction)', () => {
    expect(new _ExtrinsicExtra.default().toU8a()).toEqual(new Uint8Array([0, 0, 0]));
  });
  it('encodes to a sane Uint8Array (struct in)', () => {
    expect(new _ExtrinsicExtra.default({
      nonce: 1,
      tip: 2
    }).toU8a()).toEqual(new Uint8Array([0, 4, 8])); // numbers are Compact
  });
  it('encodes to a sane Uint8Array (Uint8Array in)', () => {
    expect(new _ExtrinsicExtra.default(new Uint8Array([0, 8, 4])).toU8a()).toEqual(new Uint8Array([0, 8, 4]));
  });
});